var roi = ee.FeatureCollection('FAO/GAUL/2015/level1').filter(ee.Filter.eq('ADM1_CODE',12));
var region = roi.geometry();

function processPrecipitationData(year) {
    return ee.Image(`NASA/GLDAS/V021/NOAH/G025/T3H/${year}`)
        .select('precipitation')
        .clip(region)
        .multiply(8760);
}

var years = ee.List.sequence(2000,2018);
var precipitationImages = years.map(processPrecipitationData);

var annualMeans = precipitationImages.map(function(image){
    return image.reduceRegion({
        reducer: ee.Reducer.mean(),
        geometry: region,
        scale:1000
    });
});

var lineChart = ui.Chart.array.values({
    array: ee.Array(annualMeans),
    axis:0,
    xLabels:years
}).setChartType('LineChart').setOptions({
    title:'Annual Precipitation in the Tianjin Region (2000-2018)',
    hAxis:{title:'Year'},
    vAxis:{title:'Annual Precipitation (mm)'}
});
