var wuhanBoundary = ee.FeatureCollection('users/your_username/wuhan_boundary');
var calculateNDVI = function(image){
  return image.normalizedDifference(['SR_B5','SR_B4']).rename('NDVI');
};

var calculateVegetationCover = function(year){
  var ndviMedian = ee.ImageCollection('LANDSAT/LC08/C02/T1_L2')
    .filterDate(ee.Date.fromYMD(year,1,1), ee.Date.fromYMD(year,12,31))
    .filterBounds(wuhanBoundary)
    .map(calculateNDVI).median().clip(wuhanBoundary);

  var stats = ndviMedian.gt(0.3)
    .reduceRegion({
      reducer: ee.Reducer.sum().combine(ee.Reducer.count(),'',true),
      geometry: wuhanBoundary,
      scale:30
    });
  return ee.Number(stats.get('NDVI')).divide(ee.Number(stats.get('NDVI_count')));
};
