// Module 1: Define Region of Interest (ROI) and Initial Map Setup
// This module initializes the ROI and sets up the map visualization.
var geometry = ee.Geometry.Polygon([[[109.7, 38.8], [110.3, 38.8], [110.3, 38.4], [109.7, 38.4], [109.7, 38.8]]]);
var roi = ee.FeatureCollection(ee.Feature(geometry));
Map.centerObject(geometry, 9);
Map.addLayer(roi, {color: "000000", fillColor: "000000"}, "ROI", false);

// Module 2: Process Images for NDVI and Desertification Index
// This module calculates NDVI for a specific year, applies cloud masking
function processImage(year, roi) {
    var l8Col = ee.ImageCollection("LANDSAT/LC08/C01/T1_SR")
        .filterBounds(roi)
        .filterDate(year + "-04-01", year + "-10-01")
        .select(["B4", "B5", "QA60"])
        .map(function(image) {
            var b4 = image.select("B4");
            var b5 = image.select("B5");
            var ndvi = b5.subtract(b4).divide(b5.add(b4));
            var cloudMask = image.select("QA60").bitwiseAnd(1 << 4).eq(0);  // QA60 is used for cloud masking
            ndvi = ndvi.updateMask(cloudMask).updateMask(ndvi.lt(0.1));  // Mask out low NDVI
            return ndvi.rename("ndvi");
        });

    var yearImg = l8Col.max().clip(roi);
    return yearImg;
}

// Module 4: Calculate Desertification Area
// This module calculates the desertification area (NDVI < 0.1) for each year.
var img1 = processImage(2015, geometry);
Map.addLayer(img1, {min: -1, max: 1, palette: "c7c7c7"}, "2015", false); // grey
var img2 = processImage(2016, geometry);
Map.addLayer(img2, {min: -1, max: 1, palette: "ffff00"}, "2016", false); // yellow
var img3 = processImage(2017, geometry);
Map.addLayer(img3, {min: -1, max: 1, palette: "0000ff"}, "2017", false); // blue
var img4 = processImage(2018, geometry);
Map.addLayer(img4, {min: -1, max: 1, palette: "00ff00"}, "2018", false); // green
var img5 = processImage(2019, geometry);
Map.addLayer(img5, {min: -1, max: 1, palette: "ff0000"}, "2019", false); // red
Map.addLayer(roi, {color: "000000", fillColor: "000000"}, "roi", true);

// Module 4: Calculate Desertification Area
// This module calculates the desertification area for each year.
function calcArea(yearImg, roi) {
    var areaImage = yearImg.pixelArea().multiply(yearImg.lt(0.1));
    var area = areaImage.reduceRegion({
        reducer: ee.Reducer.sum(),
        geometry: roi,
        scale: 30
    });
    return area;
}

// Module 5: Compute and Print Desertification Area for 2015–2019
// This module computes the annual desertification area and logs the results.
var areaValues = [
    calcArea(img1, geometry),
    calcArea(img2, geometry),
    calcArea(img3, geometry),
    calcArea(img4, geometry),
    calcArea(img5, geometry)
];

var xSeries = [2015, 2016, 2017, 2018, 2019];
areaValues.forEach(function(area, index) {
    print('Year ' + (2015 + index) + ' Desertification Area: ', area);
});

// Module 6: Visualize Desertification Trends with a Line Chart
// This module visualizes the annual desertification area changes using a line chart.
var lineChart = ui.Chart.array.values({
    array: ee.Array(areaValues).slice(0, 0),
    axis: 0
}).setOptions({
    title: 'Desertification area change in northern Shaanxi from 2015 to 2019',
    legend: ["Desertification area"],
    vAxis: {title: "Desertification area (km²)"},
    hAxis: {title: "Year"},
    lineWidth: 2
});
lineChart.style().set({width: '800px', height: '400px'});
print(lineChart);
