// 1. 数据加载：读取武汉的行政区和道路数据
var city_shp = ee.FeatureCollection("users/your_username/Wuhanqu");  // 需要上传Wuhanqu.shp文件到GEE
var roads_shp = ee.FeatureCollection("users/your_username/Wuhanroad");  // 需要上传Wuhanroad.shp文件到GEE

// 2. 投影转换：GEE会自动处理CRS，但我们可以明确指定EPSG:4527
city_shp = city_shp.transform('EPSG:4527');
roads_shp = roads_shp.transform('EPSG:4527');

// 3. 数据清理：去除无效的道路几何
roads_shp = roads_shp.filter(ee.Filter.notNull(['geometry']));

// 4. 道路长度计算：计算道路的长度（单位：km）
var roads_length = roads_shp.map(function(feature) {
  var length = feature.geometry().length().divide(1000);  // 转换为公里
  return feature.set('length_km', length);
});

// 5. 行政区面积计算：计算每个行政区的面积（单位：km²）
city_shp = city_shp.map(function(feature) {
  var area = feature.geometry().area().divide(1e6);  // 转换为km²
  return feature.set('area_km2', area);
});

// 6. 空间连接：将道路数据与行政区空间连接
var roads_in_city = roads_shp.filterBounds(city_shp.geometry());

// 7. 聚合计算：计算每个行政区内的总道路长度
var road_length_by_area = city_shp.map(function(feature) {
  var roads_in_district = roads_in_city.filterBounds(feature.geometry());
  var total_road_length = roads_in_district.aggregate_sum('length_km');
  return feature.set('total_road_length_km', total_road_length);
});

// 8. 道路密度计算：计算道路密度（单位：km/km²）
road_length_by_area = road_length_by_area.map(function(feature) {
  var road_density = feature.get('total_road_length_km').divide(feature.get('area_km2'));
  return feature.set('road_density_km_per_km2', road_density);
});

// 9. 可视化道路密度的主题地图
Map.centerObject(city_shp, 10);  // 设置地图中心
Map.addLayer(road_length_by_area, 
             {color: 'FF0000', 
              min: 0, max: 10, 
              palette: ['white', 'red']}, 
             'Road Density');

// 10. 创建条形图：按道路密度排序显示武汉各区的道路密度
var sorted = road_length_by_area.sort('road_density_km_per_km2', false);
var chart = ui.Chart.feature.byFeature(sorted, 'road_density_km_per_km2')
               .setChartType('Bar')
               .setOptions({
                 title: 'Road Density by Administrative District in Wuhan',
                 vAxis: {title: 'Road Density (km/km²)'},
                 hAxis: {title: 'District'},
                 legend: {position: 'none'}
               });
print(chart);
