// ================== Module 1: Data Retrieval ==================
var france = ee.FeatureCollection('FAO/GAUL/2015/level1').filter(ee.Filter.eq('ADM0_NAME', 'France'));
var treeCover = ee.Image('MODIS/006/MOD44B').select('Percent_Tree_Cover');

// ================== Module 2: Data Processing ==================
Map.centerObject(france, 6);
Map.addLayer(treeCover, {min: 0, max: 100, palette: ['white', 'green']}, 'Tree Cover');
var treeCoverMasked = treeCover.clip(france);
Map.addLayer(treeCoverMasked, {min: 0, max: 100, palette: ['white', 'green']}, 'Forest Cover (Cropped)');

// ================== Module 3: Zonal Statistics ==================
var zonalStats = treeCoverMasked.reduceRegions({
  collection: france,
  reducer: ee.Reducer.sum(),
  scale: 500
});
var forestStats = zonalStats.map(function(feature) {
  var area = feature.get('sum');
  var area_km2 = ee.Number(area).multiply(1e-6);
  return feature.set('Forest_Area_km2', area_km2);
});
print('Forest Cover Statistics:', forestStats);

// ================== Module 4: Visualization ==================
Map.addLayer(forestStats, {min: 0, max: 1000, palette: ['white', 'green', 'brown', 'darkgreen']}, 'Forest Area by Region');
