//提取6个城市的MCD12Q2中50%的sos和eos 

var roi=ee.FeatureCollection("users/jiyingying0421/6citiesMCD12Q2/Beijing_s");

var startDate = 2010+'-01-01' 
var endDate = 2010+'-12-31' 
var sos = ee.ImageCollection('MODIS/006/MCD12Q2') 
    .filterBounds(roi)  //可能由于这个数据集就是一片的，filterbounds不等于mask 
    .filterDate(startDate, endDate) 
    .select('MidGreenup_1')
var sos1city = sos.first().clip(roi)

print("sos1city",sos1city)
Map.addLayer(sos1city,{min: 10000, max: 19000, palette: ['FF0000','ffFF00']},'sos1city');

//eos
var eos = ee.ImageCollection('MODIS/006/MCD12Q2') 
    .filterBounds(roi)  //可能由于这个数据集就是一片的，filterbounds不等于mask 
    .filterDate(startDate, endDate) 
    .select('MidGreendown_1')
print("eos",eos)    
var eos1city = eos.first().clip(roi)
print("eos1city",eos1city)
Map.addLayer(eos1city,{min: 10000, max: 19000, palette: ['FF0000','ffFF00']},'eos1city');


//导出做好的栅格影像
Export.image.toDrive({
  image: sos1city,
  description: 'Beijingsos',
  scale: 500,
  region: roi,
  maxPixels: 1e13
});

//导出做好的栅格影像
Export.image.toDrive({
  image: eos1city,
  description: 'Beijingeos',
  scale: 500,
  region: roi,
  maxPixels: 1e13
});
