var boundaries = ee.FeatureCollection('FAO/GAUL/2015/level1');
var population = ee.Image('WorldPop/GP/100m/pop').select('population').divide(1000);

var processed = boundaries.map(function(feature) {
  var stats = population.reduceRegion({
    reducer: ee.Reducer.mean(),
    geometry: feature.geometry(),
    scale: 1000
  });
  return feature.set('pop_density', stats.get('population'));
});

var chart = ui.Chart.feature.byProperty({
  features: processed,
  xProperties: ['ADM1_NAME'],
  yProperties: ['pop_density']
}).setChartType('ColumnChart')
 .setOptions({
   title: 'Provincial Population Density',
   hAxis: {title: 'Province'},
   vAxis: {title: 'Density (people/km²)'}
 });

print(chart);
