var start = ee.Date('2021-01-01');

var finish = ee.Date('2021-5-30');

var thailand = ee.FeatureCollection('USDOS/LSIB_SIMPLE/2017').filterMetadata('country_co', 'equals', 'TH');


var dataset = ee.ImageCollection('LANDSAT/LC08/C01/T1_SR')
        .filterDate(start, finish)
        .select(['B5','B4'])
        .filterMetadata('CLOUD_COVER', 'less_than', 20)
        .filterBounds(thailand)
        .median();

var NDVI = dataset.normalizedDifference(['B5', 'B4']);

Map.setCenter(100, 13, 5);

Map.addLayer(NDVI, { min: -1, max: 1 }, 'NDVI (Thailand)');

print(NDVI)

var NDVIVis = {
  min: -0.5,
  max: 1,
  palette: [
    '040274', '040281', '0502a3', '0502b8', '0502ce', '0502e6',
    '0602ff', '235cb1', '307ef3', '269db1', '30c8e2', '32d3ef',
