var imageCollection = ee.ImageCollection("LANDSAT/LC08/C02/T1_TOA"),
    geometry = 
    ee.Geometry.Polygon(
        [[[-67.14354011184994, 83.84416037333492],
          [-67.14354011184994, 57.7748231181012],
          [-16.870102611849944, 57.7748231181012],
          [-16.870102611849944, 83.84416037333492]]], null, false);

var data = imageCollection.filterBounds(geometry).filterDate("2014-04-10","2022-4-10")
var data2014 = data.filterDate ("2014-01-01","2014-12-31")
var data2022 = data.filterDate ("2022-01-01","2022-12-31")
var data2018 = data.filterDate ("2018-01-01","2018-12-31")

var ndsi = function(image){
  return image.normalizedDifference(['B3','B6']).rename('NDSI');
};

var snow2014 = data2014.map(ndsi).min()
var snow2022 = data2022.map(ndsi).min()
var snow2018 = data2018.map(ndsi).min()

Map.addLayer(snow2014.gt(0),{},"2014");
Map.addLayer(snow2018.gt(0),{},"2018");
Map.addLayer(snow2022.gt(0),{},"2022");
