var geometry = ee.FeatureCollection("projects/ee-moabbas/assets/agri_laval");
var collection = ee.ImageCollection('COPERNICUS/S1_GRD')
  .filterDate('2021-01-01', '2021-12-31')
  .filterBounds(geometry)
  .select(['VV', 'VH']);

var calculateRVI4S1 = function(image) {
  var VV = image.select('VV');
  var VH = image.select('VH');
  var dop = (VV.divide(VV.add(VH))).multiply(-1).add(1);
  var m=(1).subtract(dop)
  var value = dop.sqrt().multiply((VH.multiply(4)).divide(VV.add(VH)));
  var visParams = {
    min: 0,
    max: 1,
    palette: ['black', 'blue', 'green', 'yellow', 'orange', 'red']
  };
  return value.visualize(visParams);
};

var RVI4S1 = collection.map(calculateRVI4S1);
Map.addLayer(RVI4S1.first(), {}, 'RVI4S1');
