/**** Start of imports. If edited, may not auto-convert in the playground. ****/
var polygon = ee.FeatureCollection("users/mtrs1000220/Karnataka_dist_polygon");
/***** End of imports. If edited, may not auto-convert in the playground. *****/
var shp = ee.FeatureCollection (polygon)

Map.addLayer (shp , {} , 'My Polygon')

var dataset = ee.ImageCollection('MODIS/006/MOD11A2')

                  .filter(ee.Filter.date('2018-09-20', '2018-10-04'));

var landSurfaceTemperature = dataset.select('LST_Day_1km');

var indialstr = landSurfaceTemperature.reduce(ee.Reducer.mean())

var indialst = indialstr.clip(polygon)

var landSurfaceTemperatureVis = {
  min: 13000.0,
  max: 16500.0,
  palette: [
    '040274', '040281', '0502a3', '0502b8', '0502ce', '0502e6',
    '0602ff', '235cb1', '307ef3', '269db1', '30c8e2', '32d3ef',
    '3be285', '3ff38f', '86e26f', '3ae237', 'b5e22e', 'd6e21f',
    'fff705', 'ffd611', 'ffb613', 'ff8b13', 'ff6e08', 'ff500d',
    'ff0000', 'de0101', 'c21301', 'a71001', '911003'
  ],
};

Map.setCenter(6.746, 46.529, 2);
Map.addLayer(
    indialst, landSurfaceTemperatureVis,
    'Land Surface Temperature');

Export.image.toDrive({
  image: indialst,
  description: 'image',
  scale: 1000,
  region: polygon
});
