var table = ee.FeatureCollection("users/ncr/qilianshan"),
    geometry = /* color: #d63000 */ee.Geometry.MultiPoint();
/***** End of imports. If edited, may not auto-convert in the playground. *****/
Map.addLayer(table, {color: 'FF0000', fillColor: '00000000', width: 1}, "ROI")

function maskL8sr(image) {

  // Bits 3 and 5 are cloud shadow and cloud, respectively.

  var cloudShadowBitMask = (1 << 3);

  var cloudsBitMask = (1 << 5);

  // Get the pixel QA band.

  var qa = image.select('pixel_qa');

  // Both flags should be set to zero, indicating clear conditions.

  var mask = qa.bitwiseAnd(cloudShadowBitMask).eq(0)
                 .and(qa.bitwiseAnd(cloudsBitMask).eq(0));

  return image.updateMask(mask);
}



var dataset = ee.ImageCollection('LANDSAT/LC08/C01/T1_SR')
    .filterDate('2020-01-01', '2020-10-31')
    .filterMetadata('CLOUD_COVER','less_than',5)
    .map(function(image){
                      return image.clip(table)//裁剪
                  })
    .map(maskL8sr)             


var visParams = {
  bands: ['B4', 'B3', 'B2'],
  min: 0,
  max: 3000,
  gamma: 1.4,
};

Map.centerObject(table, 6);

Map.addLayer(dataset, visParams, '祁连山');
