/**** Start of imports. If edited, may not auto-convert in the playground. ****/
var srtm = ee.Image("CGIAR/SRTM90_V4"),
    water = ee.Image("JRC/GSW1_0/GlobalSurfaceWater"),
    geometry = /* color: #d63000 */ee.Geometry.Polygon(
        [[[-99.14023674751556, 26.804927831107772],
          [-99.29825873437669, 26.816922506543893],
          [-99.27671064297638, 26.577599041115178],
          [-99.16054603703279, 26.545887778535814],
          [-99.07493436275246, 26.574949775093465]]]);
/***** End of imports. If edited, may not auto-convert in the playground. *****/
//////////////// Analysis ////////////////
var slope = ee.Terrain.slope(srtm);

// Statistics for an area
var slopeDict = slope.reduceRegion({
  reducer: ee.Reducer.mean(),
  geometry: geometry,
  scale: 30
});
print('slopeDict', slopeDict);

var waterDict = water.select('occurrence').gt(90)
    .multiply(ee.Image.pixelArea())
    .reduceRegion({
      reducer: ee.Reducer.sum(),
      geometry: geometry,
      scale: 30
    });
print('waterDict', waterDict);

//////////////// User Interface ////////////////
Map.addLayer(srtm, {min:0, max:3000}, 'srtm');
Map.addLayer(slope, {min:0, max:45}, 'slope');
Map.addLayer(water, {bands:'occurrence', min:25, max:75, palette:'lightblue,blue'}, 'occurrence');
